mapper.insert:
INSERT INTO sf_menu
( name
, parents_id
, url
, is_used
, sort_sequence
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES

  ( #{item.name}
  , #{item.parentsId}
  , #{item.url}
  , #{item.isUsed}
  , #{item.sortSequence}
  , #{item.createdBy}
  , #{item.createdDate}
  , #{item.lastModifiedBy}
  , #{item.lastModifiedDate})

update.foreach:
UPDATE sf_menu
SET name               = #{item.name}
  , parents_id         = #{item.parentsId}
  , url                = #{item.url}
  , is_used            = #{item.isUsed}
  , sort_sequence      = #{item.sortSequence}
  , last_modified_by   = #{item.lastModifiedBy}
  , last_modified_date = #{item.lastModifiedDate}
WHERE id = #{item.id}

