mapper.select:
SELECT id , name , is_deleted , deleted_by , deleted_date , remark , created_by , created_date , last_modified_by , last_modified_date FROM sf_role WHERE is_deleted = #{isDeleted}
AND id = #{id}
AND name LIKE CONCAT('%', #{name}, '%')

mapper.insert:
INSERT INTO sf_role
( name
, is_deleted
, remark
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES

( #{item.name}
, #{item.isDeleted}
, #{item.remark}
, #{item.createdBy}
, #{item.createdDate}
, #{item.lastModifiedBy}
, #{item.lastModifiedDate})

INSERT INTO sf_role
( id
, name
, is_deleted
, remark
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES 

( #{item.id}
, #{item.name}
, #{item.isDeleted}
, #{item.remark}
, #{item.createdBy}
, #{item.createdDate}
, #{item.lastModifiedBy}
, #{item.lastModifiedDate})

update.foreach:
UPDATE sf_role
SET name               = #{item.name}
  , remark             = #{item.remark}
  , last_modified_by   = #{item.lastModifiedBy}
  , last_modified_date = #{item.lastModifiedDate}
WHERE id = #{item.id}

UPDATE sf_role
SET is_deleted         = #{item.isDeleted}
  , deleted_date       = #{item.deletedDate}
  , deleted_by         = #{item.deletedBy}
  , last_modified_by   = #{item.lastModifiedBy}
  , last_modified_date = #{item.lastModifiedDate}
WHERE id = #{item.id}
