mapper.insert:
    INSERT INTO sf_user
	  ( user_id
      , password
      , name
      , email
      , phone_number
      , created_by
      , created_date
      , last_modified_by
      , last_modified_date)
    VALUES ( #{userId}
           , #{password}
           , #{name}
           , #{email}
           , #{phoneNumber}
           , #{createdBy}
           , current_timestamp()
           , #{lastModifiedBy}
           , current_timestamp())

    INSERT INTO sf_user
	  ( id
	    , user_id
      , password
      , name
      , email
      , phone_number
      , created_by
      , created_date
      , last_modified_by
      , last_modified_date)
    VALUES ( #{id}
           , #{userId}
           , #{password}
           , #{name}
           , #{email}
           , #{phoneNumber}
           , #{createdBy}
           , current_timestamp()
           , #{lastModifiedBy}
           , current_timestamp())

mapper.update:
UPDATE sf_user
SET user_id             = #{userId}
  , password            = #{password}
  , name                = #{name}
  , email               = #{email}
  , phone_number        = #{phoneNumber}
  , last_modified_by    = #{lastModifiedBy}
  , last_modified_date  = current_timestamp()
WHERE id = #{id}

UPDATE sf_user
SET is_deleted          = #{isDeleted}
  , deleted_by          = #{deletedBy}
  , deleted_date        = current_timestamp()
  , last_modified_by   = #{lastModifiedBy}
  , last_modified_date = current_timestamp()
WHERE id = #{id}
