mapper.insert:
INSERT INTO sf_sample
( sample_key
, sample_value
, comment
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES ( #{sampleKey}
       , #{sampleValue}
       , #{comment}
       , #{createdBy}
       , current_timestamp()
       , #{lastModifiedBy}
       , current_timestamp())

INSERT INTO sf_sample
( id
, sample_key
, sample_value
, comment
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES ( #{id}
       , #{sampleKey}
       , #{sampleValue}
       , #{comment}
       , #{createdBy}
       , current_timestamp()
       , #{lastModifiedBy}
       , current_timestamp())

mapper.update:
UPDATE sf_sample
SET sample_key         = #{sampleKey}
  , sample_value       = #{sampleValue}
  , comment            = #{comment}
  , last_modified_by   = #{lastModifiedBy}
  , last_modified_date = current_timestamp()
WHERE id = #{id}
